import React,{useState} from "react";

function FileUpload() {
  const [fileList, setFileList] = useState([]);

  const handleFileChange = ({fileList, file }) => {
    setFileList([...fileList, file]);
  }

  return (
    <div id="upload-box">
      <input type="file" onChange={handleFileChange} />
    </div>
  );
}


export default function App() {
  return <FileUpload />;
}
