import {useRef} from "react";


const Drag = ({handleChange}) => {
  const drop = useRef(null);
  console.log(drop);
  const dropRef = document.querySelector('.drag-drop')

    const handleDragEnter = () => dropRef.classList.add("dragover");
    const handleLeave = () => dropRef.classList.remove("dragover");
    const handleDrop = (e) => {
      dropRef.classList.remove("dragover");
      e.target.value = null;
    };
  return (
    <div
      className="drag-drop"
      ref={drop}
      onDrop={handleDrop}
      onDragEnter={handleDragEnter}
      onDragLeave={handleLeave}
    >
      <input
        type="file"
        id="input-file-upload"
        multiple
        onChange={handleChange}
      />
      <label id="label-file-upload" htmlFor="input-file-upload">
        <p>Drag and drop your file here</p>
      </label>
    </div>
  );
};

export default Drag;
