import React, { useRef, useState } from "react";
import plus from "./plus.svg";
import trash from "./delete.svg";
import eye from "./eye.svg";
import close from "./close.svg";
const FileUpLoad = () => {
  const [images, setImages] = useState([]);
  const [isToggle, setIsToggle] = useState(false);
  const [item, setItem] = useState();
  const [isChange, setIsChange] = useState(false);

  const drop = useRef(null);
  console.log(drop);

  function handleChange(e) {
    const files = e.target.files;
    for (let i = 0; i < files.length; i++) {
      images.push({ id: images.length + 1, data: files[i] });
    }
    setImages([...images]);
    console.log(images);
  }

  const handleDelete = (id) => {
    const newData = images.filter((item) => item.id !== id);
    setImages(newData);
    console.log(newData);
  };

  const handleDetail = (id) => {
    const imgDetail = images.find((item) => item.id === id);
    setItem(imgDetail);
    console.log(imgDetail);
  };

  const handleDragEnter = () => drop.current.classList.add("dragover");
  const handleLeave = () => drop.current.classList.remove("dragover");
  const handleDrop = (e) => {
    drop.current.classList.remove("dragover");
    e.target.value = null;
  };
console.log(images);
  return (
    <>
      <div className="change-layout">
        <button
          onClick={() => setIsChange(!isChange)}
          className="btn btn-change"
        >
          Change Layout
        </button>
      </div>
      {!isChange ? (
        <>
          <h4 className="title">Upload File</h4>
          <label htmlFor="image" className="btn btn-upload">
            <img src={plus} alt="" className="plus-icon" />
            Upload
          </label>
          <input
            className="input-form"
            type="file"
            id="image"
            multiple
            accept="image/*"
            onChange={handleChange}
            style={{ display: "none" }}
          />
          <ul className="img-list">
            {images &&
              images.map((image, index) => (
                <li className="img-item" key={index}>
                  <a className="img-box" href="/#">
                    <img
                      className="img-up"
                      src={URL.createObjectURL(image.data)}
                      alt=""
                    />
                    <img
                      src={trash}
                      alt=""
                      onClick={() => handleDelete(image.id)}
                      className="delete-icon"
                    />
                    <img
                      src={eye}
                      className="eye-icon"
                      id="eye-modal"
                      alt=""
                      onClick={() => {
                        handleDetail(image.id);
                        setIsToggle(!isToggle);
                      }}
                    />
                  </a>
                </li>
              ))}
          </ul>
          {!isToggle ? (
            <div className="modal dis-none"></div>
          ) : (
            <div className="modal dis-block">
              <div className="modal-content">
                <div className="img-content">
                  <h4 className="img-name">
                    {item?.data.name}
                    <span className="img-size">{item?.data.size} MB</span>
                  </h4>
                  <img
                    onClick={() => setIsToggle(!isToggle)}
                    src={close}
                    alt=""
                    className="close"
                  />
                </div>
                <img
                  className="img-detail"
                  src={URL.createObjectURL(item?.data)}
                  alt=""
                />
              </div>
            </div>
          )}
        </>
      ) : (
        <>
          <div
            className="drag-drop"
            ref={drop}
            onDrop={handleDrop}
            onDragEnter={handleDragEnter}
            onDragLeave={handleLeave}
          >
            <input
              type="file"
              id="input-file-upload"
              multiple
              onChange={handleChange}
            />
          <label id="label-file-upload" htmlFor="input-file-upload">
            <p>Drag and drop your file here</p>
          </label>
          </div>
        </>
      )}
    </>
  );
};

export default FileUpLoad;
