import React, { useRef } from "react";
import trash from "./delete.svg";
import eye from "./eye.svg";
const ImageItem = ({
  images,
  setIsToggle,
  isToggle,
  handleDelete,
  handleDetail,
}) => {
  const delRef = useRef();
  const handleClick = () => {
    const timeId = setTimeout(() => {
      setIsToggle(!isToggle);
    }, 500);

    delRef.current?.classList.add("opacity");
    return () => {
      window.clearTimeout(timeId);
    };
  };
  return (
    <ul className="img-list">
      {images &&
        images.map((image, index) => (
          <li className="img-item" key={index} ref={delRef}>
            <a className="img-box" href="/#">
              <img
                className="img-up"
                src={URL.createObjectURL(image.data)}
                alt=""
              />
              <img
                src={trash}
                alt=""
                onClick={() => {handleDelete(image.id)}, handleClick}
                className="delete-icon"
              />
              <img
                src={eye}
                className="eye-icon"
                id="eye-modal"
                alt=""
                onClick={() => {
                  handleDetail(image.id);
                  setIsToggle(!isToggle);
                }}
              />
            </a>
            <h4 className="img-item-name">
              {image.data.name.length > 25
                ? image.data.name.substring(0, 10) +
                  "..." +
                  image.data.name.substring(44, image.data.length)
                : image.data.name}
            </h4>
          </li>
        ))}
    </ul>
  );
};

export default ImageItem;
