import React, { useState } from "react";
import plus from "./plus.svg";
import trash from "./delete.svg";
import eye from "./eye.svg";
import close from "./close.svg";
const FileUpLoad = () => {
  const [images, setImages] = useState([]);
  const [isToggle, setIsToggle] = useState(false);

  function handleChange(e) {
    const files = e.target.files;
    for (let i = 0; i < files.length; i++) {
      images.push({ id: images.length + 1, data: files[i] });
    }
    setImages([...images]);
    console.log(images);
  }

  const handleDelete = (id) => {
    const newData = images.filter((item) => item.id !== id);
    setImages(newData);
    console.log(newData);
  };

//   const handleDetail = (id) => {
//     // const modal = document.getElementById("img-modal");
//     // // const btn = document.getElementById("eye-modal");
//     // // const close = document.getElementsByClassName(".close");
//     // modal.style.display = "block";
//     const modal = document.getElementById("img-modal");
//     const close = document.getElementsByClassName(".close");

//   };

  return (
    <>
      <label htmlFor="image" className="btn btn-upload">
        <img src={plus} alt="" className="plus-icon" />
        Upload
      </label>
      <input
        className="input-form"
        type="file"
        id="image"
        multiple
        accept="image/*"
        onChange={handleChange}
        style={{ display: "none" }}
      />
      <ul className="img-list">
        {images &&
          images.map((image, index) => (
            <li className="img-item" key={index}>
              <a className="img-box" href="/#">
                <img
                  className="img-up"
                  src={URL.createObjectURL(image.data)}
                  alt=""
                />
                <img
                  src={trash}
                  alt=""
                  onClick={() => handleDelete(image.id)}
                  className="delete-icon"
                />
                <img
                  src={eye}
                  className="eye-icon"
                  id="eye-modal"
                  alt=""
                  onClick={() => setIsToggle(!isToggle)}
                />
                {!isToggle }
                <div id="img-modal" className="modal">
                  <div className="modal-content">
                    <h4 className="img-title">Image Title</h4>
                    <img src={close} alt="" className="close" />
                    <p className="img-name">{image.data.name}</p>
                    <p className="img-size">{image.data.size}</p>
                  </div>
                </div>
              </a>
            </li>
          ))}
      </ul>
    </>
  );
};

export default FileUpLoad;
