import React, { useRef } from 'react'
import close from './close.svg';
const Modal = ({item,  setIsToggle ,isToggle}) => {
  const ref = useRef();
  const handleClick = ref.current.classList.add("opacity");
  return (
    <div className="modal is-show">
              <div className="modal-content ">
                <div className="img-content">
                  <h4 className="img-name">
                    {item?.data.name}
                    <span className="img-size">{item?.data.size} MB</span>
                  </h4>
                  <img
                    onClick={() => {setIsToggle(!isToggle); handleClick}}
                    src={close}
                    alt=""
                    className="close"
                  />
                </div>
                <img
                  className="img-detail"
                  src={URL.createObjectURL(item?.data)}
                  alt=""
                />
              </div>
            </div>
  )
}

export default Modal
