import React, { useState, useEffect } from "react";
import plus from "./plus.svg";
import trash from "./delete.svg";

const FileUpLoad = () => {
  const [images, setImages] = useState([]);
  const [imageURLS, setImageURLs] = useState([]);

  useEffect(() => {
    if (images.length < 1) return;
    const newImageUrls = [];
    images.forEach((image) => newImageUrls.push(URL.createObjectURL(image)));
    setImageURLs(newImageUrls);
  }, [images]);

  function handleChange(e) {
    // console.log(e.target.files);
    setImages([...images,...e.target.files]);
  }
//   console.log(images);

  const handleDelete = (images) => {
    // const newImage = [];
        // images.filter((image) => newImage !== image)
        console.log(images);
  }

  return (
    <>
      <label htmlFor="image" className="btn btn-upload">
        <img src={plus} alt="" className="plus-icon" />
        Upload
      </label>
      <input
        className="input-form"
        type="file"
        id="image"
        multiple
        accept="image/*"
        onChange={handleChange}
        style={{ display: "none" }}
      />
      <ul className="image-list">
        {imageURLS.map((image, index) => (
          <li className="image-item col-6" key={index}>
            <img src={image} alt="" width={"200px"} />
            <img src={trash} alt="" onClick={handleDelete} className="delete-icon"  />
          </li>
        ))}
      </ul>
    </>
  );
};

export default FileUpLoad;
