import React, { useEffect } from 'react'
import trash from "./delete.svg";
import eye from "./eye.svg";
import { onScroll } from '../../Animation/FadeIn';
const ImageItem = ({images, setIsToggle ,isToggle, handleDelete, handleDetail}) => {
    useEffect(() => {
        const handlerScroll =()=>{
          const animateItem = document.querySelectorAll();
          const animateImg = document.querySelectorAll(".needs-img-item");
        onScroll(animateItem)
        onScroll(animateImg)
        }

        window.addEventListener("scroll", handlerScroll);
        return () => window.removeEventListener("scroll",handlerScroll );
    },[]);
  return (
    <ul className="img-list">
    {images &&
      images.map((image, index) => (
        <li className="img-item" key={index}>
          <a className="img-box" href="/#">
            <img
              className="img-up"
              src={URL.createObjectURL(image.data)}
              alt=""
            />
            <img
              src={trash}
              alt=""
              onClick={() => handleDelete(image.id)}
              className="delete-icon"
            />
            <img
              src={eye}
              className="eye-icon"
              id="eye-modal"
              alt=""
              onClick={() => {
                handleDetail(image.id);
                setIsToggle(!isToggle);
              }}
            />
          </a>
        </li>
      ))}
  </ul>
  )
}

export default ImageItem
