import React, { useState } from "react";
import plus from "./plus.svg";
import trash from "./delete.svg";

const FileUpLoad = () => {
  const [images, setImages] = useState([]);

  function handleChange(e) {
    const files = e.target.files;
    for (let i = 0; i < files.length; i++) {
      images.push({ id: i, data: files[i] });
    }
    setImages([...images]);
  }

  const handleDelete = (id) => {
    const newData = images.filter((item) => item.id !== id)
    setImages(newData);
  };

  return (
    <>
      <label htmlFor="image" className="btn btn-upload">
        <img src={plus} alt="" className="plus-icon" />
        Upload
      </label>
      <input
        className="input-form"
        type="file"
        id="image"
        multiple
        accept="image/*"
        onChange={handleChange}
        style={{ display: "none" }}
      />
      <ul className="image-list">
        {images.map((image, index) => (
          <li className="image-item col-6" key={index}>
            <img className="img-up" src={URL.createObjectURL(image.data)} alt="" />
            <img
              src={trash}
              alt=""
              onClick={() => handleDelete(image.id)}
              className="delete-icon"
            />
          </li>
        ))}
      </ul>
    </>
  );
};

export default FileUpLoad;
