import React from "react";

const FileUpLoad = ({ files, setFiles }) => {
  const uploadHander = (event, prev) => {
    const file = event.target.files;
    setFiles([...prev, ]);
  };

  const uploadFiles = (event) => {
    for (let i = 0; i < files[0]; i++) {
      files.push(URL.createObjectURL(files[0][i]));
    }
    setFiles([...files]);
  };
  return (
    <form className="file-card">
      <div className="form-group">
        <input
          type="file"
          className="form-control"
          onChange={uploadHander}
          multiple
        />
      </div>
      <button
        type="button"
        className="btn btn-danger btn-block"
        onClick={uploadFiles}
      >
        Upload
      </button>
      {files && 
      <ul>
        {files.map((url) => (
            <img src={url} alt="" />
        ))}
        </ul>
        }
    </form>
  );
};

export default FileUpLoad;
