import React from "react";
import trash from "./delete.svg";
import eye from "./eye.svg";
const ImageItem = ({
  images,
  setIsToggle,
  isToggle,
  handleDelete,
  handleDetail,
}) => {
    console.log(images);
    const length = images.map(item => item.data.name.length)
  return (
    <ul className="img-list">
      {images &&
        images.map((image, index) => (
          <li className="img-item" key={index}>
            <a className="img-box" href="/#">
              <img
                className="img-up"
                src={URL.createObjectURL(image.data)}
                alt=""
              />
              <img
                src={trash}
                alt=""
                onClick={() => handleDelete(image.id)}
                className="delete-icon"
              />
              <img
                src={eye}
                className="eye-icon"
                id="eye-modal"
                alt=""
                onClick={() => {
                  handleDetail(image.id);
                  setIsToggle(!isToggle);
                }}
              />
            </a>
            <span className="img-item-name">
              {length[0] > 20
                ? image?.data.name.substring(0, 10) +
                  "..." +
                  image(30, image.data.length)
                : image.data.name}
                {image.data.name}
            </span>
          </li>
        ))}
    </ul>
  );
};

export default ImageItem;
